    float darkeyeColor = darkeye.sample(sam, uv).r;
    float sharpenColor = sharpen.sample(sam, uv).r;
    float eyeColor = eye.sample(sam, uv).r;
    float whitenerColor = whitener.sample(sam, uv).r;

//    float4 drawing = float4(0);
        float3 drawing = float3(0);
    if(retouchId == 0){
        drawing.r = smoothColor;
    }else if(retouchId == 1){
        drawing.r = spotColor;
    }else if(retouchId == 2){
            drawing.r = darkeyeColor;
    }else if(retouchId == 3){
            drawing.r = sharpenColor;
    }else if(retouchId == 4){
            drawing = eyeColor;
    }else if(retouchId == 5){
            drawing = whitenerColor;
    }

    float4 color = img.sample(sam, uvS );
    float i = img.get_width() / smooth.get_width();
    float dark = (((color.r + color.g + color.b)/3));
    float dsark = (1. - ((color.r + color.g + color.b)))/3;
    float3 smoothedCol = smoothskin(img,  uv,  (12.*spotColor) ,i);
    float3 blured = color.rgb - blur(img,  uv, 5);
    color.rgb = mix(color.rgb, smoothedCol - blured  ,smoothColor*dark) ;
    color.rgb = mix(color.rgb, smoothedCol      ,spotColor);
    color.rgb = mix(color.rgb, smoothedCol   - dsark/12.    ,darkeyeColor);
    color.rgb = mix(color.rgb, smoothedCol      ,darkeyeColor);
    color.rgb = mix(color.rgb, smoothedCol - dsark ,whitenerColor);
